import React, { useContext } from 'react';
import { Link } from 'react-router-dom';
import { AuthContext } from '../context/AuthContext';
import ThemeToggle from './ThemeToggle';
import SearchBar from './SearchBar';

function Navbar({ dark, setDark }) {
  const { user, logout } = useContext(AuthContext);

  const linkStyle = {
    color: '#fff',
    fontWeight: 600,
    textDecoration: 'none',
    padding: '6px 12px',
    borderRadius: 6,
    transition: 'background 0.3s',
  };

  const hoverEffect = (e) => {
    e.target.style.background = 'rgba(255,255,255,0.2)';
  };

  const leaveEffect = (e) => {
    e.target.style.background = 'transparent';
  };

  const buttonStyle = {
    padding: '6px 14px',
    background: '#fff',
    color: '#0B1E3D',
    border: 'none',
    borderRadius: 6,
    cursor: 'pointer',
    fontWeight: 600,
    textDecoration: 'none',
    transition: 'background 0.3s, color 0.3s',
  };

  const buttonHover = (e) => {
    e.target.style.background = '#0B1E3D';
    e.target.style.color = '#fff';
  };

  const buttonLeave = (e) => {
    e.target.style.background = '#fff';
    e.target.style.color = '#0B1E3D';
  };

  return (
    <nav
      style={{
        padding: 12,
        display: 'flex',
        gap: 12,
        alignItems: 'center',
        background: 'linear-gradient(90deg,#0A84FF, #0B1E3D)',
        flexWrap: 'wrap',
      }}
    >
      <Link
        to="/"
        style={{ ...linkStyle, fontSize: 18, fontWeight: 700 }}
        onMouseEnter={hoverEffect}
        onMouseLeave={leaveEffect}
      >
        Cyberpidia
      </Link>
      <Link to="/tools" style={linkStyle} onMouseEnter={hoverEffect} onMouseLeave={leaveEffect}>Tools</Link>
      <Link to="/books" style={linkStyle} onMouseEnter={hoverEffect} onMouseLeave={leaveEffect}>Books</Link>
      <Link to="/courses" style={linkStyle} onMouseEnter={hoverEffect} onMouseLeave={leaveEffect}>Courses</Link>

      <div style={{ flex: 1, minWidth: 200 }}>
        <SearchBar />
      </div>

      <ThemeToggle dark={dark} setDark={setDark} />

      {user ? (
        <>
          <span style={{ color: '#fff', marginLeft: 8 }}>{user.email}</span>
          <button
            onClick={logout}
            style={buttonStyle}
            onMouseEnter={buttonHover}
            onMouseLeave={buttonLeave}
          >
            Logout
          </button>
        </>
      ) : (
        <>
          <Link
            to="/login"
            style={{ ...buttonStyle, marginLeft: 8 }}
            onMouseEnter={buttonHover}
            onMouseLeave={buttonLeave}
          >
            Login
          </Link>
          <Link
            to="/signup"
            style={{ ...buttonStyle, marginLeft: 8 }}
            onMouseEnter={buttonHover}
            onMouseLeave={buttonLeave}
          >
            Sign Up
          </Link>
        </>
      )}

      {user?.role === 'admin' && (
        <Link
          to="/admin"
          style={{ ...buttonStyle, marginLeft: 8 }}
          onMouseEnter={buttonHover}
          onMouseLeave={buttonLeave}
        >
          Admin
        </Link>
      )}
    </nav>
  );
}

export default Navbar;
