import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

export default function SearchBar(){
  const [q, setQ] = useState('');
  const navigate = useNavigate();

  const handleSearch = async (e) => {
    e?.preventDefault();
    if(!q) return;
    // navigate to a search results page? for now just call API and log
    const res = await axios.get(`${process.env.REACT_APP_API_URL || 'http://localhost:5000'}/api/search?query=${encodeURIComponent(q)}`);
    // save results in sessionStorage for demonstration & navigate to tools page
    sessionStorage.setItem('cyber_search_results', JSON.stringify(res.data));
    navigate('/tools');
  };

  return (
    <form onSubmit={handleSearch} style={{ display:'flex' }}>
      <input value={q} onChange={(e)=>setQ(e.target.value)} placeholder="Search tools, books, courses..." style={{ padding:8, width: '100%' }} />
      <button type="submit" style={{ padding:8 }}>Search</button>
    </form>
  );
}
