import React from "react";
import { motion } from "framer-motion";

export default function Home() {
  return (
    <div style={{ padding: 20, maxWidth: 1200, margin: "0 auto" }}>
      <motion.h1
        initial={{ opacity: 0, y: -20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.7 }}
        style={{ color: "#0A84FF", fontSize: "3rem", fontWeight: "bold", marginBottom: 20 }}
      >
        Welcome to Cyberpidia
      </motion.h1>

      <motion.p
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ duration: 1, delay: 0.3 }}
        style={{ fontSize: "1.25rem", lineHeight: 1.8, color: "#ffffffcc", marginBottom: 40 }}
      >
        Your cybersecurity tools, courses, and books directory with guides, roadmaps, and tutorials to help you become a cybersecurity expert.
      </motion.p>

      {/* Sections */}
      <motion.div
        initial={{ opacity: 0, x: -50 }}
        animate={{ opacity: 1, x: 0 }}
        transition={{ duration: 0.7, delay: 0.5 }}
        style={{ marginBottom: 40 }}
      >
        <h2 style={{ color: "#0af", fontSize: "2rem", marginBottom: 10 }}>1. What is Cyberpidia?</h2>
        <p style={{ color: "#e0e0e0", fontSize: "1.1rem" }}>
          Cyberpidia is your one-stop hub for learning and exploring cybersecurity. From tools to books and online courses, we provide all resources in one place.
        </p>
      </motion.div>

      <motion.div
        initial={{ opacity: 0, x: 50 }}
        animate={{ opacity: 1, x: 0 }}
        transition={{ duration: 0.7, delay: 0.7 }}
        style={{ marginBottom: 40 }}
      >
        <h2 style={{ color: "#0af", fontSize: "2rem", marginBottom: 10 }}>2. Enhance Your Knowledge</h2>
        <p style={{ color: "#e0e0e0", fontSize: "1.1rem" }}>
          Access a wide range of cybersecurity resources including tools, books, courses, and tutorials to expand your knowledge and skills.
        </p>
      </motion.div>

      <motion.div
        initial={{ opacity: 0, y: 50 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.7, delay: 0.9 }}
      >
        <h2 style={{ color: "#0af", fontSize: "2rem", marginBottom: 10 }}>3. Start Your Cybersecurity Journey Today</h2>
        <p style={{ color: "#e0e0e0", fontSize: "1.1rem" }}>
          Whether you are a beginner or an advanced learner, Cyberpidia helps you kickstart your cybersecurity career with the right resources and guidance.
        </p>
      </motion.div>
    </div>
  );
}
