import React, { useContext } from "react";
import { Link } from "react-router-dom";
import { AuthContext } from "../context/AuthContext";
import ThemeToggle from "./ThemeToggle";
import SearchBar from "./SearchBar";

function Navbar({ dark, setDark }) {
  const { user, logout } = useContext(AuthContext);

  const buttonStyle = {
    padding: "6px 12px",
    background: "#fff",
    color: "#0B1E3D",
    border: "none",
    borderRadius: 4,
    cursor: "pointer",
    fontWeight: 600,
    textDecoration: "none",
  };

  return (
    <nav
      style={{
        padding: 12,
        display: "flex",
        gap: 12,
        alignItems: "center",
        background: "linear-gradient(90deg,#0A84FF, #0B1E3D)",
      }}
    >
      <Link
        to="/"
        style={{ color: "#fff", fontWeight: 700, textDecoration: "none" }}
      >
        Cyberpidia
      </Link>
      <Link to="/tools" style={{ color: "#fff" }}>
        Tools
      </Link>
      <Link to="/books" style={{ color: "#fff" }}>
        Books
      </Link>
      <Link to="/courses" style={{ color: "#fff" }}>
        Courses
      </Link>
      <div style={{ flex: 1 }}>
        <SearchBar />
      </div>
      <ThemeToggle dark={dark} setDark={setDark} />

      {!user && (
        <>
          <Link to="/login" style={{ ...buttonStyle, marginLeft: 8 }}>
            Login
          </Link>
          <Link to="/signup" style={{ ...buttonStyle, marginLeft: 8 }}>
            Sign Up
          </Link>
        </>
      )}

      {user ? (
        <>
          <span style={{ color: "#fff" }}>{user.email}</span>
          <button onClick={logout} style={buttonStyle}>
            Logout
          </button>
        </>
      ) : (
        <Link to="/login" style={{ ...buttonStyle, marginLeft: 8 }}>
          Login
        </Link>
      )}

      {user?.role === "admin" && (
        <Link to="/admin" style={{ ...buttonStyle, marginLeft: 8 }}>
          Admin
        </Link>
      )}
    </nav>
  );
}

export default Navbar;
