import React, { useEffect, useState } from 'react';
import axios from 'axios';

export default function Tools() {
  const [tools, setTools] = useState([]);

  useEffect(() => {
    const cached = sessionStorage.getItem('cyber_search_results');
    if (cached) {
      const parsed = JSON.parse(cached);
      if (parsed.tools) { 
        setTools(parsed.tools); 
        return; 
      }
    }

    axios.get(`${process.env.REACT_APP_API_URL || 'http://localhost:5000'}/api/tools`)
      .then(res => setTools(res.data))
      .catch(err => console.error(err));
  }, []);

  return (
    <div style={{ padding: '40px 20px', fontFamily: 'Arial, sans-serif', minHeight: '100vh', background: '#f9f9f9' }}>
      <h1 style={{ textAlign: 'center', marginBottom: 40, color: '#0B1E3D' }}>Cybersecurity Tools</h1>

      <div style={{ 
        display: 'grid', 
        gridTemplateColumns: 'repeat(auto-fit, minmax(250px, 1fr))', 
        gap: 20 
      }}>
        {tools.map(tool => (
          <div key={tool._id} style={{ 
            background: '#fff', 
            borderRadius: 12, 
            padding: 20, 
            boxShadow: '0 4px 10px rgba(0,0,0,0.1)', 
            transition: 'transform 0.3s, box-shadow 0.3s'
          }}
          onMouseEnter={e => {
            e.currentTarget.style.transform = 'translateY(-5px)';
            e.currentTarget.style.boxShadow = '0 8px 20px rgba(0,0,0,0.15)';
          }}
          onMouseLeave={e => {
            e.currentTarget.style.transform = 'translateY(0)';
            e.currentTarget.style.boxShadow = '0 4px 10px rgba(0,0,0,0.1)';
          }}
          >
            <h3 style={{ color: '#0A84FF', marginBottom: 10 }}>{tool.name}</h3>
            <p style={{ color: '#333', marginBottom: 10 }}>{tool.summary}</p>
            <a 
              href={tool.url || '#'} 
              target="_blank" 
              rel="noreferrer" 
              style={{
                color: '#fff',
                background: '#0A84FF',
                padding: '8px 16px',
                borderRadius: 8,
                textDecoration: 'none',
                fontWeight: 600,
                display: 'inline-block',
                transition: 'background 0.3s'
              }}
              onMouseEnter={e => e.currentTarget.style.background = '#0666d0'}
              onMouseLeave={e => e.currentTarget.style.background = '#0A84FF'}
            >
              Visit Tool
            </a>
          </div>
        ))}
      </div>
    </div>
  );
}
