import React, { useContext, useState } from 'react';
import { Link } from 'react-router-dom';
import { AuthContext } from '../context/AuthContext';
import ThemeToggle from './ThemeToggle';
import SearchBar from './SearchBar';

function Navbar({ dark, setDark }){
  const { user, logout } = useContext(AuthContext);
  return (
    <nav style={{ padding: 12, display:'flex', gap:12, alignItems:'center', background: 'linear-gradient(90deg,#0A84FF, #0B1E3D)' }}>
      <Link to="/" style={{ color:'#fff', fontWeight:700, textDecoration:'none' }}>Cyberpidia</Link>
      <Link to="/tools" style={{ color:'#fff' }}>Tools</Link>
      <Link to="/books" style={{ color:'#fff' }}>Books</Link>
      <Link to="/courses" style={{ color:'#fff' }}>Courses</Link>
      <div style={{ flex:1 }}><SearchBar /></div>
      <ThemeToggle dark={dark} setDark={setDark} />
      {user ? (
        <>
          <span style={{ color:'#fff' }}>{user.email}</span>
          <button onClick={logout}>Logout</button>
        </>
      ) : (
        <Link to="/login" style={{ color:'#fff' }}>Login</Link>
      )}
      {user?.role === 'admin' && <Link to="/admin" style={{ color:'#fff', marginLeft: 8 }}>Admin</Link>}
    </nav>
  );
}

export default Navbar;
