import React, { useContext, useState } from 'react';
import { Link } from 'react-router-dom';
import { AuthContext } from '../context/AuthContext';
import ThemeToggle from './ThemeToggle';
import SearchBar from './SearchBar';

function Navbar({ dark, setDark }) {
  const { user, logout } = useContext(AuthContext);
  const [menuOpen, setMenuOpen] = useState(false);

  const linkStyle = {
    color: '#fff',
    fontWeight: 600,
    textDecoration: 'none',
    padding: '6px 12px',
    borderRadius: 6,
    transition: 'background 0.3s',
    display: 'block'
  };

  const buttonStyle = {
    padding: '6px 16px',
    background: '#fff',
    color: '#0B1E3D',
    border: 'none',
    borderRadius: 8,
    cursor: 'pointer',
    fontWeight: 600,
    textDecoration: 'none',
    transition: 'all 0.3s ease',
    boxShadow: '0 2px 5px rgba(0,0,0,0.15)',
    display: 'block',
    textAlign: 'center',
    marginTop: 8
  };

  return (
    <nav style={{ padding: 12, background: 'linear-gradient(90deg,#0A84FF, #0B1E3D)' }}>
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
        <Link to="/" style={{ color: '#fff', fontWeight: 700, fontSize: 20 }}>Cyberpidia</Link>
        <div className="desktop-menu" style={{ display: 'flex', alignItems: 'center', gap: 12 }}>
          <div className="menu-links" style={{ display: 'flex', gap: 12 }}>
            <Link to="/tools" style={linkStyle}>Tools</Link>
            <Link to="/books" style={linkStyle}>Books</Link>
            <Link to="/courses" style={linkStyle}>Courses</Link>
          </div>
          <div style={{ minWidth: 200 }}><SearchBar /></div>
          <ThemeToggle dark={dark} setDark={setDark} />
          {user ? (
            <>
              <span style={{ color: '#fff', marginLeft: 8 }}>{user.email}</span>
              <button onClick={logout} style={buttonStyle}>Logout</button>
            </>
          ) : (
            <>
              <Link to="/login" style={buttonStyle}>Login</Link>
              <Link to="/signup" style={buttonStyle}>Sign Up</Link>
            </>
          )}
          {user?.role === 'admin' && <Link to="/admin" style={buttonStyle}>Admin</Link>}
        </div>

        {/* Hamburger Icon for Mobile */}
        <button
          className="hamburger"
          onClick={() => setMenuOpen(!menuOpen)}
          style={{
            display: 'none', // will show in mobile via media query
            background: 'transparent',
            border: 'none',
            fontSize: 28,
            color: '#fff',
            cursor: 'pointer'
          }}
        >
          â˜°
        </button>
      </div>

      {/* Mobile Menu */}
      {menuOpen && (
        <div className="mobile-menu" style={{ marginTop: 12 }}>
          <Link to="/tools" style={linkStyle}>Tools</Link>
          <Link to="/books" style={linkStyle}>Books</Link>
          <Link to="/courses" style={linkStyle}>Courses</Link>
          <div style={{ marginTop: 8 }}><SearchBar /></div>
          <ThemeToggle dark={dark} setDark={setDark} />
          {user ? (
            <>
              <span style={{ color: '#fff', display: 'block', marginTop: 8 }}>{user.email}</span>
              <button onClick={logout} style={buttonStyle}>Logout</button>
            </>
          ) : (
            <>
              <Link to="/login" style={buttonStyle}>Login</Link>
              <Link to="/signup" style={buttonStyle}>Sign Up</Link>
            </>
          )}
          {user?.role === 'admin' && <Link to="/admin" style={buttonStyle}>Admin</Link>}
        </div>
      )}

      {/* Media Queries */}
      <style>
        {`
          @media (max-width: 768px) {
            .desktop-menu { display: none; }
            .hamburger { display: block; }
          }
          @media (min-width: 769px) {
            .mobile-menu { display: none; }
          }
        `}
      </style>
    </nav>
  );
}

export default Navbar;
