import React, { useContext, useState } from 'react';
import { Link } from 'react-router-dom';
import { AuthContext } from '../context/AuthContext';
import ThemeToggle from './ThemeToggle';
import SearchBar from './SearchBar';

function Navbar({ dark, setDark }) {
  const { user, logout } = useContext(AuthContext);
  const [menuOpen, setMenuOpen] = useState(false);

  const linkStyle = {
    color: '#fff',
    fontWeight: 600,
    textDecoration: 'none',
    padding: '6px 12px',
    borderRadius: 6,
    transition: 'background 0.3s',
    display: 'block',
  };

  const buttonStyle = {
    padding: '6px 16px',
    background: '#fff',
    color: '#0B1E3D',
    border: 'none',
    borderRadius: 8,
    cursor: 'pointer',
    fontWeight: 600,
    textDecoration: 'none',
    transition: 'all 0.3s ease',
    boxShadow: '0 2px 5px rgba(0,0,0,0.15)',
    margin: '4px 0',
    width: '100%',
    textAlign: 'center',
  };

  const buttonHover = (e) => {
    e.target.style.background = '#0B1E3D';
    e.target.style.color = '#fff';
    e.target.style.boxShadow = '0 4px 8px rgba(0,0,0,0.2)';
  };
  const buttonLeave = (e) => {
    e.target.style.background = '#fff';
    e.target.style.color = '#0B1E3D';
    e.target.style.boxShadow = '0 2px 5px rgba(0,0,0,0.15)';
  };

  return (
    <nav style={{ background: 'linear-gradient(90deg,#0A84FF, #0B1E3D)', padding: 12 }}>
      <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between' }}>
        <Link to="/" style={{ color: '#fff', fontWeight: 700, fontSize: 18, textDecoration: 'none' }}>
          Cyberpidia
        </Link>

        <div className="desktop-only" style={{ display: 'flex', alignItems: 'center', gap: 12 }}>
          <Link to="/tools" style={linkStyle}>Tools</Link>
          <Link to="/books" style={linkStyle}>Books</Link>
          <Link to="/courses" style={linkStyle}>Courses</Link>
          <div style={{ minWidth: 200 }}><SearchBar /></div>
          <ThemeToggle dark={dark} setDark={setDark} />
          {user ? (
            <>
              <span style={{ color: '#fff' }}>{user.email}</span>
              <button
                onClick={logout}
                style={buttonStyle}
                onMouseEnter={buttonHover}
                onMouseLeave={buttonLeave}
              >
                Logout
              </button>
            </>
          ) : (
            <>
              <Link to="/login" style={buttonStyle} onMouseEnter={buttonHover} onMouseLeave={buttonLeave}>Login</Link>
              <Link to="/signup" style={buttonStyle} onMouseEnter={buttonHover} onMouseLeave={buttonLeave}>Sign Up</Link>
            </>
          )}
          {user?.role === 'admin' && (
            <Link to="/admin" style={buttonStyle} onMouseEnter={buttonHover} onMouseLeave={buttonLeave}>Admin</Link>
          )}
        </div>

        {/* Hamburger for mobile */}
        <button
          className="mobile-only"
          onClick={() => setMenuOpen(!menuOpen)}
          style={{ background: 'transparent', border: 'none', color: '#fff', fontSize: 24, cursor: 'pointer' }}
        >
          ☰
        </button>
      </div>

      {/* Mobile menu */}
      {menuOpen && (
        <div className="mobile-only" style={{ display: 'flex', flexDirection: 'column', gap: 4, marginTop: 8 }}>
          <Link to="/tools" style={linkStyle}>Tools</Link>
          <Link to="/books" style={linkStyle}>Books</Link>
          <Link to="/courses" style={linkStyle}>Courses</Link>
          <SearchBar />
          <ThemeToggle dark={dark} setDark={setDark} />
          {user ? (
            <>
              <span style={{ color: '#fff' }}>{user.email}</span>
              <button
                onClick={logout}
                style={buttonStyle}
                onMouseEnter={buttonHover}
                onMouseLeave={buttonLeave}
              >
                Logout
              </button>
            </>
          ) : (
            <>
              <Link to="/login" style={buttonStyle} onMouseEnter={buttonHover} onMouseLeave={buttonLeave}>Login</Link>
              <Link to="/signup" style={buttonStyle} onMouseEnter={buttonHover} onMouseLeave={buttonLeave}>Sign Up</Link>
            </>
          )}
          {user?.role === 'admin' && (
            <Link to="/admin" style={buttonStyle} onMouseEnter={buttonHover} onMouseLeave={buttonLeave}>Admin</Link>
          )}
        </div>
      )}
    </nav>
  );
}

export default Navbar;
